import React, { useState } from 'react'
import { View, StyleSheet, TouchableOpacity, Text, ScrollView, SafeAreaView, Image } from 'react-native'
import Constants from 'expo-constants'
import { useNavigation } from '@react-navigation/native'
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

import Mapa from '../../components/Menu/Mapa'
import Ferramentas from '../../components/Menu/Ferramentas'
import Saude from '../../components/Menu/Saude'

const Stack = createStackNavigator();



const Navigation = () => {

    const navigation = useNavigation()
    const [isMapSelected, setMapSelected] = useState<boolean>(true)
    const [isToolsSelected, setToolsSelected] = useState<boolean>(false)
    const [isHealthSelected, setHealthSelected] = useState<boolean>(false)


    function handleNavigateMapa() {
        setMapSelected(true)
        setToolsSelected(false)
        setHealthSelected(false)
        navigation.navigate('Mapa')
    }


    function handleNavigateSaude() {
        setHealthSelected(true)
        setMapSelected(false)
        setToolsSelected(false)
        navigation.navigate('Saude')
    }


    function handleNavigateFerramentas() {
        setToolsSelected(true)
        setMapSelected(false)
        setHealthSelected(false)
        navigation.navigate('Ferramentas')
    }

    return (
        <SafeAreaView style={{ flex: 1 }} >

            <View style={styles.container}>
                <NavigationContainer>
                    <Stack.Navigator>
                        <Stack.Screen
                            name="Home"
                            component={Home}
                            options={{ title: 'Welcome' }}
                        />
                        <Stack.Screen name="Profile" component={Profile} />
                    </Stack.Navigator>
                </NavigationContainer>
            </View>

            <View style={styles.menuContainer}>
                <ScrollView
                    horizontal
                    showsHorizontalScrollIndicator={false}
                    contentContainerStyle={{
                        paddingHorizontal: 20
                    }}>

                    <TouchableOpacity
                        style={[
                            styles.item,
                            isHealthSelected ? styles.selectedItem : {}
                        ]}
                        onPress={() => handleNavigateSaude()}
  activeOpacity={0.5}
                    >
                        <Image source={require('../../icons/saude.png')} style={styles.imagem} />
                        <Text style={styles.menuTitle}>Saúde</Text>
                    </TouchableOpacity>

                    <TouchableOpacity
                        style={[
                            styles.item,
                            isMapSelected ? styles.selectedItem : {}
                        ]}
                        onPress={() => handleNavigateMapa()}
  activeOpacity={0.5}
                    >
                        <Image source={require('../../icons/mapa.png')} style={styles.imagem} />

                        <Text style={styles.menuTitle}>Mapa</Text>
                    </TouchableOpacity>

                    <TouchableOpacity
                        style={[
                            styles.item,
                            isToolsSelected ? styles.selectedItem : {}
                        ]}

                        onPress={() => handleNavigateFerramentas()}
  activeOpacity={0.5}
                    >
                        <Image source={require('../../icons/ferramentas.png')} style={styles.imagem} />

                        <Text style={styles.menuTitle}>Ferramentas</Text>
                    </TouchableOpacity>

                </ScrollView>

            </View>


        </SafeAreaView>
    )
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingHorizontal: 32,
        paddingTop: 20 + Constants.statusBarHeight,
    },

    menuContainer: {
        flexDirection: 'row',
        marginTop: 16,
        marginBottom: 32,
    },

    item: {
        backgroundColor: '#fff',
        borderWidth: 2,
        borderColor: '#eee',
        height: 120,
        width: 120,
        borderRadius: 8,
        paddingHorizontal: 16,
        paddingTop: 20,
        paddingBottom: 16,
        marginRight: 8,
        alignItems: 'center',
        justifyContent: 'space-between',
        textAlign: 'center',
    },

    selectedItem: {
        borderColor: '#34CB79',
        borderWidth: 2,
    },

    menuTitle: {
        fontFamily: 'Roboto_400Regular',
        textAlign: 'center',
        fontSize: 13,
    },

    imagem: {
        width: 64,
        height: 64
    },
});

export default Navigation;
