    },

    itemTitle: {
        fontFamily: 'Roboto_400Regular',
        textAlign: 'center',
        fontSize: 15,
    },

    imagem: {
        width: 42,
        height: 42
    },


    button: {
        width: 80,
        height: 40,
        backgroundColor: '#FFF',
        alignItems: 'center',
